	 public function getTableCategory($category_id) {
$query = $this->db->query(" SELECT p.product_id, p.model,p.size,p.oe,p.obd,p.osd,p.w,p.a,p.b,p.l,p.upc,p.ean,p.jan,p.isbn,p.mpn,p.o,p.articul_dop, p.d1, p.range,p.norma,p.color,p.name_short,p.thickness
FROM " . DB_PREFIX . "product p 
LEFT JOIN " . DB_PREFIX . "product_to_category ptc ON (p.product_id = ptc.product_id)
LEFT JOIN " . DB_PREFIX . "category c  ON (ptc.category_id = c.category_id)
LEFT JOIN " . DB_PREFIX . "category_description cd ON (c.category_id = cd.category_id) 
LEFT JOIN " . DB_PREFIX . "category_to_store c2s ON (c.category_id = c2s.category_id) 
WHERE 
c.category_id = '" . (int)$category_id . "' AND cd.language_id = '" . (int)$this->config->get('config_language_id') . "' AND c2s.store_id = '" . (int)$this->config->get('config_store_id') . "' AND p.status = '1'
ORDER BY 
model");

       return $query->rows;
    }
